CREATE TABLE `countries` (
  `code` varchar(2) CHARACTER SET ascii NOT NULL DEFAULT '',
  `flag` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL,
  `country_iso` varchar(255) CHARACTER SET utf8mb4 NOT NULL DEFAULT '',
  `country_ar` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_cs` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_de` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_en` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_es` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_fr` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_it` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_ja` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_nl` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_pt` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_ru` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_sk` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_zh-cn` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `country_zh-hk` varchar(255) CHARACTER SET utf8mb4 NOT NULL,
  `latitude` double NOT NULL DEFAULT '0',
  `longitude` double NOT NULL DEFAULT '0',
  `elevation` int(11) NOT NULL,
  `continent` varchar(2) CHARACTER SET ascii NOT NULL,
  `locale` varchar(512) CHARACTER SET ascii NOT NULL,
  `dialcode` varchar(9) CHARACTER SET ascii NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;



ALTER TABLE `countries`
  ADD PRIMARY KEY (`code`),
  ADD UNIQUE KEY `code_2` (`code`),
  ADD KEY `code` (`code`);
COMMIT;


